import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { menuAPI } from '../services/api';
import MenuItem from './MenuItem';
import CategoryFilter from './CategoryFilter';
import RestaurantHeader from './RestaurantHeader';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';

const MenuPage = () => {
  const { slug } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState({});
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchMenuData();
  }, [slug]);

  const fetchMenuData = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await menuAPI.getMenu(slug);
      
      if (response.success) {
        setRestaurant(response.restaurant);
        setCategories(response.categories);
      } else {
        setError(response.message || 'Failed to load menu');
      }
    } catch (err) {
      setError(err.message || 'Failed to load menu');
    } finally {
      setLoading(false);
    }
  };

  const getFilteredItems = () => {
    if (selectedCategory === 'all') {
      // Return all items from all categories
      return Object.values(categories).flat();
    }
    return categories[selectedCategory] || [];
  };

  const getCategoryNames = () => {
    return Object.keys(categories);
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} />;
  }

  if (!restaurant) {
    return <ErrorMessage message="Restaurant not found" />;
  }

  const filteredItems = getFilteredItems();
  const categoryNames = getCategoryNames();

  return (
    <div className="min-h-screen bg-gray-50">
      <RestaurantHeader restaurant={restaurant} />
      
      <div className="container mx-auto px-4 py-6">
        <CategoryFilter
          categories={categoryNames}
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
        />
        
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mt-6">
          {filteredItems.map((item) => (
            <MenuItem key={item.id} item={item} />
          ))}
        </div>
        
        {filteredItems.length === 0 && (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">
              {selectedCategory === 'all' 
                ? 'No menu items available' 
                : `No items found in ${selectedCategory} category`
              }
            </p>
          </div>
        )}
      </div>
      
      <footer className="bg-white border-t border-gray-200 mt-12">
        <div className="container mx-auto px-4 py-6">
          <div className="text-center">
            <p className="text-sm text-gray-500">
              Powered by <span className="font-semibold text-blue-600">SmartMenu</span>
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default MenuPage;
