import React, { useState, useEffect } from 'react';
import VegNonVegIcon from './VegNonVegIcon';

const DebugMenu = () => {
  const [menuData, setMenuData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMenuData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/menu/Shree-Krishna-Palace');
        const data = await response.json();
        
        console.log('üîç Full API Response:', data);
        
        if (data.success) {
          setMenuData(data);
          console.log('üìä Menu Categories:', data.categories);
          console.log('üçΩÔ∏è Sample Items:', data.categories?.desserts?.slice(0, 3));
        } else {
          setError(data.message);
        }
      } catch (err) {
        console.error('‚ùå Error fetching menu:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchMenuData();
  }, []);

  if (loading) return <div>Loading debug data...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!menuData) return <div>No data received</div>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>üîç Debug Menu Data</h1>
      
      <h2>Restaurant Info:</h2>
      <pre>{JSON.stringify(menuData.restaurant, null, 2)}</pre>
      
      <h2>Categories:</h2>
      <pre>{JSON.stringify(Object.keys(menuData.categories || {}), null, 2)}</pre>
      
      <h2>Sample Items from Desserts:</h2>
      {menuData.categories?.desserts?.slice(0, 5).map((item, index) => (
        <div key={index} style={{ 
          border: '1px solid #ccc', 
          padding: '10px', 
          margin: '10px 0',
          display: 'flex',
          alignItems: 'center',
          gap: '10px'
        }}>
          <VegNonVegIcon isVeg={item.is_veg} />
          <div>
            <strong>{item.name}</strong> - ‚Çπ{item.price}
            <br />
            <small>is_veg: {JSON.stringify(item.is_veg)} (type: {typeof item.is_veg})</small>
            {item.description && <div>Description: {item.description}</div>}
          </div>
        </div>
      ))}
    </div>
  );
};

export default DebugMenu;
