import React from 'react';

const VegNonVegIcon = ({ isVeg }) => {
  // Debug: Log the isVeg value
  console.log('VegNonVegIcon Debug:', { isVeg, type: typeof isVeg });
  
  // If isVeg is null or undefined, don't show any icon
  if (isVeg === null || isVeg === undefined) {
    console.log('VegNonVegIcon: No icon (null/undefined)');
    return null;
  }

  if (isVeg === true || isVeg === 1 || isVeg === '1' || isVeg === 'true') {
    console.log('VegNonVegIcon: Rendering VEGETARIAN icon');
    // Vegetarian - Green circle in a green square border
    return (
      <div 
        style={{
          display: 'inline-flex',
          alignItems: 'center',
          justifyContent: 'center',
          width: '20px',
          height: '20px',
          border: '3px solid #22c55e',
          borderRadius: '3px',
          flexShrink: 0,
          backgroundColor: '#f0fdf4',
          marginRight: '8px'
        }}
        title="Vegetarian"
      >
        <div 
          style={{
            width: '8px',
            height: '8px',
            backgroundColor: '#22c55e',
            borderRadius: '50%'
          }}
        />
      </div>
    );
  } else {
    console.log('VegNonVegIcon: Rendering NON-VEGETARIAN icon');
    // Non-Vegetarian - Red triangle in a red square border
    return (
      <div 
        style={{
          display: 'inline-flex',
          alignItems: 'center',
          justifyContent: 'center',
          width: '20px',
          height: '20px',
          border: '3px solid #ef4444',
          borderRadius: '3px',
          flexShrink: 0,
          backgroundColor: '#fef2f2',
          marginRight: '8px'
        }}
        title="Non-Vegetarian"
      >
        <div 
          style={{
            width: 0,
            height: 0,
            borderLeft: '4px solid transparent',
            borderRight: '4px solid transparent',
            borderBottom: '7px solid #ef4444'
          }}
        />
      </div>
    );
  }
};

export default VegNonVegIcon;
