import React, { useState } from 'react';
import api from '../services/api';

const StarRating = ({ rating, setRating, label, required = true }) => {
  const [hover, setHover] = useState(0);

  const getRatingLabel = (value) => {
    const labels = {
      1: 'Poor',
      2: 'Fair',
      3: 'Good',
      4: 'Very Good',
      5: 'Excellent'
    };
    return labels[value] || '';
  };

  const current = hover || rating;

  return (
    <div className="mb-3">
      <div className="flex items-center justify-between mb-2">
        <label className="text-sm font-medium text-gray-700">
          {label} {required && <span className="text-red-500">*</span>}
        </label>
        {current > 0 && (
          <span className="text-xs font-semibold text-amber-700 px-3 py-1 bg-amber-100 rounded-full">
            {getRatingLabel(current)}
          </span>
        )}
      </div>
      <div className="flex items-center gap-1">
        {[1, 2, 3, 4, 5].map((star) => {
          const isActive = star <= current;
          return (
            <button
              key={star}
              type="button"
              aria-label={`${star} star${star > 1 ? 's' : ''}`}
              onClick={() => setRating(star)}
              onMouseEnter={() => setHover(star)}
              onMouseLeave={() => setHover(0)}
              className={`transition-all duration-200 ease-in-out focus:outline-none ${
                isActive ? 'scale-105' : 'hover:scale-110'
              }`}
            >
              <svg 
                width="28" 
                height="28" 
                viewBox="0 0 24 24" 
                fill={isActive ? '#FFD700' : 'none'}
                stroke={isActive ? '#FFD700' : '#d1d5db'}
                strokeWidth="2"
                strokeLinecap="round"
                strokeLinejoin="round"
                className="transition-all duration-200"
              >
                <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
              </svg>
            </button>
          );
        })}
      </div>
    </div>
  );
};

const FeedbackForm = ({ restaurantId, restaurantName, onClose }) => {
  const [formData, setFormData] = useState({
    phone_number: '',
    food_quality: 0,
    service: 0,
    ambiance: 0,
    pricing: 0,
    comments: ''
  });

  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);

  // Phone number validation function
  const validatePhoneNumber = (phone) => {
    if (!phone) return true; // Optional field
    const phoneRegex = /^\+91[6-9]\d{9}$/;
    return phoneRegex.test(phone);
  };

  // Format phone number as user types
  const formatPhoneNumber = (value) => {
    // Remove all non-digit characters
    const digits = value.replace(/\D/g, '');

    // If empty, return empty
    if (!digits) return '';

    // If starts with 91, format as +91
    if (digits.startsWith('91')) {
      const phoneDigits = digits.substring(2);
      if (phoneDigits.length <= 10) {
        return `+91${phoneDigits}`;
      }
    }

    // If starts with 6-9 (Indian mobile number), add +91
    if (digits.length > 0 && /^[6-9]/.test(digits)) {
      if (digits.length <= 10) {
        return `+91${digits}`;
      }
    }

    // If starts with 0, remove it and add +91
    if (digits.startsWith('0')) {
      const phoneDigits = digits.substring(1);
      if (phoneDigits.length <= 10) {
        return `+91${phoneDigits}`;
      }
    }

    return value; // Return as is if doesn't match patterns
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate phone number if provided
    if (formData.phone_number && !validatePhoneNumber(formData.phone_number)) {
      setError('Please enter a valid Indian mobile number (+91 followed by 10 digits starting with 6-9)');
      return;
    }

    // Validate all ratings are provided
    if (
      !formData.food_quality ||
      !formData.service ||
      !formData.ambiance ||
      !formData.pricing
    ) {
      setError('Please provide all ratings');
      return;
    }

    setSubmitting(true);

    try {
      const response = await api.post('/feedback/submit', {
        restaurant_id: restaurantId,
        ...formData
      });

      if (response.data.success) {
        setSubmitted(true);
        setTimeout(() => {
          onClose && onClose();
        }, 2500);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit feedback');
    } finally {
      setSubmitting(false);
    }
  };

  if (submitted) {
    return (
      <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50 animate-fade-in">
        <div className="bg-white rounded-xl shadow-2xl max-w-md w-full p-10 text-center animate-scale-in">
          <h3 className="text-3xl font-bold text-gray-900 mb-4">
            Thank You for Your Feedback!
          </h3>
          <p className="text-gray-600 text-lg">
            Your feedback has been submitted successfully.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50 overflow-y-auto animate-fade-in">
      <div className="bg-white rounded-xl shadow-2xl max-w-lg w-full my-8 animate-slide-up">
        {/* Header */}
        <div className="relative bg-white border-b border-gray-200 px-6 py-5 rounded-t-xl flex items-center justify-between">
          <div>
            <h2 className="text-xl font-bold text-gray-900">Share Your Experience</h2>
            <p className="text-gray-600 text-sm mt-1">{restaurantName}</p>
          </div>
          <button
            type="button"
            onClick={onClose}
            className="w-14 h-14 rounded-full bg-red-500 hover:bg-red-600 active:bg-red-700 flex items-center justify-center transition-all duration-200 focus:outline-none focus:ring-4 focus:ring-red-200 shadow-lg hover:shadow-xl flex-shrink-0 border-2 border-red-600"
            aria-label="Close feedback form"
          >
            <svg className="w-8 h-8 text-white" fill="none" stroke="currentColor" strokeWidth="4" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6 space-y-4">
          {/* Customer Name */}
          <div className="bg-gray-50 p-4 rounded-lg">
            <label className="block text-base font-semibold text-gray-800 mb-3">
              Your Name <span className="text-gray-500 text-sm font-normal">(Optional)</span>
            </label>
            <input
              type="text"
              value={formData.customer_name}
              onChange={(e) =>
                setFormData({ ...formData, customer_name: e.target.value })
              }
              placeholder="Enter your name"
              className="w-full px-4 py-3 text-lg border-2 border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none transition-all text-gray-900 placeholder:text-gray-400 bg-white"
            />
          </div>

          {/* Divider */}
          <div className="border-t border-gray-200 pt-3">
            <h3 className="text-base font-semibold text-gray-900 mb-1">Rate Your Experience</h3>
            <p className="text-sm text-gray-500 mb-3">Please rate the following aspects</p>
          </div>

          {/* Star Ratings */}
          <StarRating
            rating={formData.food_quality}
            setRating={(val) => setFormData({ ...formData, food_quality: val })}
            label="Food Quality"
          />

          <StarRating
            rating={formData.service}
            setRating={(val) => setFormData({ ...formData, service: val })}
            label="Service"
          />

          <StarRating
            rating={formData.ambiance}
            setRating={(val) => setFormData({ ...formData, ambiance: val })}
            label="Ambiance"
          />

          <StarRating
            rating={formData.pricing}
            setRating={(val) => setFormData({ ...formData, pricing: val })}
            label="Pricing / Value for Money"
          />

          {/* Comments */}
          <div className="pt-3 border-t border-gray-200">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Additional Comments / Suggestions{' '}
              <span className="text-gray-400 text-xs">(Optional)</span>
            </label>
            <textarea
              value={formData.comments}
              onChange={(e) =>
                setFormData({ ...formData, comments: e.target.value })
              }
              placeholder="Share your thoughts, suggestions, or any specific feedback..."
              rows="3"
              className="w-full px-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none resize-none transition-all text-gray-900 placeholder:text-gray-400"
            />
          </div>

          {/* Error Message */}
          {error && (
            <div className="p-4 bg-red-50 border border-red-200 text-red-700 rounded-lg text-center">
              <span className="font-medium text-base">{error}</span>
            </div>
          )}

          {/* Submit Button */}
          <div className="pt-4 flex justify-center">
            <button
              type="submit"
              disabled={submitting}
              className={`w-full py-3 rounded-lg font-semibold text-base text-white shadow-md transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
                submitting
                  ? 'bg-gray-400 cursor-not-allowed opacity-60'
                  : 'bg-blue-600 hover:bg-blue-700 hover:shadow-lg active:scale-[0.98]'
              }`}
            >
              {submitting ? (
                <span className="flex items-center justify-center gap-2">
                  <svg className="animate-spin h-5 w-5 text-white" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  <span>Submitting...</span>
                </span>
              ) : (
                'Submit Feedback'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FeedbackForm;

