import React from 'react';

const RestaurantInfo = ({ restaurant }) => {
  if (!restaurant) return null;

  const { address, phone, wifi_name, wifi_password, custom_sections, description } = restaurant;

  return (
    <div style={{ 
      background: '#f8f9fa', 
      padding: '16px',
      marginTop: '0'
    }}>
      <div className="container mx-auto px-4" style={{ maxWidth: 420 }}>
        {/* Description */}
        {description && (
          <div style={{ 
            marginBottom: '12px',
            textAlign: 'left'
          }}>
            <p style={{ 
              fontSize: '12px', 
              color: '#6b7280',
              lineHeight: '1.4',
              margin: '0'
            }}>
              {description}
            </p>
          </div>
        )}

        {/* Address */}
        {address && (
          <div style={{ 
            display: 'flex',
            alignItems: 'flex-start',
            gap: '4px',
            marginBottom: '6px'
          }}>
            <span style={{ 
              color: '#dc2626', 
              fontSize: '14px',
              flexShrink: 0,
              marginTop: '1px'
            }}>📍</span>
            <div style={{ 
              fontSize: '12px', 
              color: '#6b7280',
              lineHeight: '1.3',
              wordWrap: 'break-word',
              overflowWrap: 'break-word',
              flex: 1
            }}>
              {address}
            </div>
          </div>
        )}

        {/* Phone */}
        {phone && (
          <div style={{ 
            display: 'flex',
            alignItems: 'center',
            gap: '4px',
            marginBottom: '6px'
          }}>
            <span style={{ 
              color: '#dc2626', 
              fontSize: '14px',
              flexShrink: 0
            }}>📞</span>
            <a 
              href={`tel:${phone}`} 
              style={{ 
                fontSize: '12px', 
                color: '#6b7280', 
                textDecoration: 'none',
                lineHeight: '1.3'
              }}
            >
              {phone}
            </a>
          </div>
        )}

        {/* WiFi Name */}
        {wifi_name && (
          <div style={{ 
            display: 'flex',
            alignItems: 'center',
            gap: '4px',
            marginBottom: '6px'
          }}>
            <span style={{ 
              color: '#2563eb', 
              fontSize: '14px',
              flexShrink: 0
            }}>📶</span>
            <span style={{ 
              fontSize: '12px', 
              color: '#6b7280',
              lineHeight: '1.3'
            }}>
              {wifi_name}
            </span>
          </div>
        )}

        {/* WiFi Password */}
        {wifi_password && (
          <div style={{ 
            display: 'flex',
            alignItems: 'center',
            gap: '4px',
            marginBottom: '6px'
          }}>
            <span style={{ 
              color: '#f59e0b', 
              fontSize: '14px',
              flexShrink: 0
            }}>🔒</span>
            <span style={{ 
              fontSize: '12px', 
              color: '#6b7280',
              lineHeight: '1.3'
            }}>
              {wifi_password}
            </span>
          </div>
        )}

        {/* Custom Sections */}
        {custom_sections && Array.isArray(custom_sections) && custom_sections.length > 0 && (
          <div style={{ marginTop: '8px' }}>
            {custom_sections.map((section, index) => (
              <div key={index} style={{ marginBottom: '6px' }}>
                <div style={{ 
                  fontSize: '12px', 
                  fontWeight: '600', 
                  color: '#374151',
                  marginBottom: '2px'
                }}>
                  {section.title}
                </div>
                <div style={{ 
                  fontSize: '12px', 
                  color: '#6b7280',
                  lineHeight: '1.3'
                }}>
                  {section.content}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantInfo;
