import React from 'react';

const RestaurantHeader = ({ restaurant }) => {
  // Convert Unsplash page URLs to direct image URLs
  const getImageUrl = (url) => {
    if (!url) return null;
    
    // Check if it's an Unsplash page URL and convert to direct image URL
    if (url.includes('unsplash.com/photos/')) {
      const photoId = url.split('/photos/')[1]?.split('?')[0];
      if (photoId) {
        return `https://images.unsplash.com/photo-${photoId}?w=800&h=400&fit=crop&crop=center`;
      }
    }
    
    return url;
  };

  const imageUrl = getImageUrl(restaurant.image_url);
  const [imageError, setImageError] = useState(false);

  const headerImageStyle = imageUrl && !imageError ? {
    backgroundImage: `url(${imageUrl})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    position: 'relative',
    height: '200px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  } : {
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    height: '200px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  };

  return (
    <div>
      {/* Image/Background Section with only restaurant name */}
      <div style={headerImageStyle}>
        {/* Dark overlay for better text readability when using image background */}
        {restaurant.image_url && (
          <div style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0, 0, 0, 0.6)',
            zIndex: 1
          }} />
        )}
        <div style={{ position: 'relative', zIndex: 2, textAlign: 'center' }}>
          {restaurant.logo_url && (
            <div style={{ marginBottom: '16px' }}>
              <img
                src={restaurant.logo_url}
                alt={restaurant.name}
                className="rounded-full object-cover border-4 border-white shadow-lg"
                style={{ width: 80, height: 80 }}
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
            </div>
          )}
          <h1 style={{ 
            fontSize: 28, 
            fontWeight: 'bold', 
            color: 'white', 
            lineHeight: 1.2, 
            textShadow: '2px 2px 4px rgba(0,0,0,0.8)',
            letterSpacing: '1px',
            textTransform: 'uppercase'
          }}>
            {restaurant.name}
          </h1>
        </div>
      </div>
      
      {/* Description Section below the image */}
      {restaurant.description && (
        <div style={{ 
          background: '#f7f7f7', 
          padding: '20px', 
          textAlign: 'center',
          borderBottom: '1px solid #e5e7eb'
        }}>
          <div className="container mx-auto" style={{ maxWidth: 420 }}>
            <p style={{ 
              color: '#374151', 
              fontSize: 16, 
              lineHeight: 1.5,
              margin: 0
            }}>
              {restaurant.description}
            </p>
          </div>
        </div>
      )}
    </div>
  );
};

export default RestaurantHeader;
