  const getEmotionConfig = (value) => {
    const configs = {
      1: { emoji: '😞', color: 'bg-red-500', label: 'Poor', textColor: 'text-red-600' },
      2: { emoji: '😐', color: 'bg-orange-500', label: 'Fair', textColor: 'text-orange-600' },
      3: { emoji: '🙂', color: 'bg-yellow-500', label: 'Good', textColor: 'text-yellow-600' },
      4: { emoji: '😊', color: 'bg-lime-500', label: 'Very Good', textColor: 'text-lime-600' },
      5: { emoji: '😄', color: 'bg-green-500', label: 'Excellent', textColor: 'text-green-600' },
    };
    return configs[value] || null;
  };

  const current = hover || rating;
  const currentConfig = getEmotionConfig(current);

  return (
    <div className="mb-6 pb-4 border-b border-gray-100 last:border-0">
      <label className="block text-sm font-semibold text-gray-700 mb-3">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <div className="flex items-center gap-4">
        {/* Emotion Indicator */}
        <div className="flex-shrink-0">
          {current > 0 ? (
            <div className={`w-12 h-12 rounded-full ${currentConfig.color} flex items-center justify-center text-2xl shadow-lg transition-all duration-200 animate-bounce-once`}>
              {currentConfig.emoji}
            </div>
          ) : (
            <div className="w-12 h-12 rounded-full bg-gray-100 flex items-center justify-center text-2xl">
              <span className="text-gray-400">😊</span>
            </div>
          )}
        </div>

        {/* Stars */}
        <div className="flex-1">
          <div className="flex items-center gap-1 mb-1">
            {[1, 2, 3, 4, 5].map((star) => {
              const isActive = star <= current;
              const isHovered = star === hover;
              return (
                <button
                  key={star}
                  type="button"
                  aria-label={`${star} star${star > 1 ? 's' : ''}`}
                  onClick={() => setRating(star)}
                  onMouseEnter={() => setHover(star)}
                  onMouseLeave={() => setHover(0)}
                  className={`transition-all duration-200 focus:outline-none ${
                    isActive ? 'scale-110' : 'scale-100 hover:scale-105'
                  }`}
                >
                  <svg 
                    className={`w-9 h-9 transition-colors duration-200 ${
                      isActive 
                        ? 'text-yellow-400 drop-shadow-md' 
                        : 'text-gray-200'
                    }`}
                    fill={isActive ? 'currentColor' : 'none'}
                    stroke="currentColor"
                    strokeWidth={isActive ? '0' : '2'}
                    viewBox="0 0 24 24"
                  >
                    <path d="M11.48 3.499a.562.562 0 011.04 0l2.125 5.111a.563.563 0 00.475.345l5.518.442c.499.04.701.663.321.988l-4.204 3.602a.563.563 0 00-.182.557l1.285 5.385a.562.562 0 01-.84.61l-4.725-2.885a.563.563 0 00-.586 0L6.982 20.54a.562.562 0 01-.84-.61l1.285-5.386a.562.562 0 00-.182-.557l-4.204-3.602a.563.563 0 01.321-.988l5.518-.442a.563.563 0 00.475-.345L11.48 3.5z" />
                  </svg>
                </button>
              );
            })}
          </div>
          {/* Rating Label */}
          {currentConfig && (
            <div className={`text-sm font-medium ${currentConfig.textColor} transition-all duration-200`}>
              {currentConfig.label}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

const FeedbackForm = ({ restaurantId, restaurantName, onClose }) => {
  const [formData, setFormData] = useState({
    customer_name: '',
    food_quality: 0,
    service: 0,
    ambiance: 0,
    pricing: 0,
    comments: ''
  });

  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate all ratings are provided
    if (
      !formData.food_quality ||
      !formData.service ||
      !formData.ambiance ||
      !formData.pricing
    ) {
      setError('Please provide all ratings');
      return;
    }

    setSubmitting(true);

    try {
      const response = await axios.post('/api/feedback/submit', {
        restaurant_id: restaurantId,
        ...formData
      });

      if (response.data.success) {
        setSubmitted(true);
        setTimeout(() => {
          onClose && onClose();
        }, 2000);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit feedback');
    } finally {
      setSubmitting(false);
    }
  };

  if (submitted) {
    return (
      <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50 animate-fade-in">
        <div className="bg-white rounded-3xl shadow-2xl max-w-md w-full p-8 text-center animate-scale-in">
          <div className="w-20 h-20 bg-gradient-to-br from-green-400 to-emerald-500 rounded-full flex items-center justify-center mx-auto mb-5 shadow-lg">
            <svg
              className="w-11 h-11 text-white"
              fill="none"
              stroke="currentColor"
              strokeWidth={3}
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M5 13l4 4L19 7"
              />
            </svg>
          </div>
          <h3 className="text-3xl font-bold text-gray-900 mb-3">
            Thank You! 🎉
          </h3>
          <p className="text-gray-600 text-lg">
            Your feedback has been submitted successfully. We appreciate your time!
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50 overflow-y-auto animate-fade-in">
      <div className="bg-white rounded-3xl shadow-2xl max-w-2xl w-full my-8 animate-slide-up">
        {/* Header */}
        <div className="relative bg-gradient-to-br from-blue-600 via-indigo-600 to-purple-600 text-white px-8 py-6 rounded-t-3xl">
          <div className="flex justify-between items-start">
            <div>
              <h2 className="text-3xl font-bold mb-2 leading-tight">Share Your Experience</h2>
              <p className="text-blue-100 text-base flex items-center gap-2">
                <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                  <path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z" />
                </svg>
                {restaurantName}
              </p>
            </div>
            <button
              onClick={onClose}
              className="text-white/80 hover:text-white text-3xl leading-none w-10 h-10 rounded-full hover:bg-white/10 flex items-center justify-center transition-all focus:outline-none focus:ring-2 focus:ring-white/40"
              aria-label="Close"
            >
              ×
            </button>
          </div>
          {/* Decorative element */}
          <div className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-yellow-400 via-pink-400 to-purple-400"></div>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-8">
          {/* Customer Name (Optional) */}
          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-700 mb-2">
              Your Name <span className="text-gray-400 text-xs font-normal">(Optional)</span>
            </label>
            <input
              type="text"
              value={formData.customer_name}
              onChange={(e) =>
                setFormData({ ...formData, customer_name: e.target.value })
              }
              placeholder="Enter your name"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none transition-all text-gray-700 placeholder:text-gray-400"
            />
          </div>

          {/* Rating Title */}
          <div className="mb-5">
            <h3 className="text-lg font-bold text-gray-800 flex items-center gap-2">
              <svg className="w-6 h-6 text-yellow-500" fill="currentColor" viewBox="0 0 20 20">
                <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
              </svg>
              Rate Your Experience
            </h3>
            <p className="text-sm text-gray-500 mt-1">Please rate the following aspects</p>
          </div>

          {/* Star Ratings */}
          <StarRating
            rating={formData.food_quality}
            setRating={(val) => setFormData({ ...formData, food_quality: val })}
            label="Food Quality"
          />

          <StarRating
            rating={formData.service}
            setRating={(val) => setFormData({ ...formData, service: val })}
            label="Service"
          />

          <StarRating
            rating={formData.ambiance}
            setRating={(val) => setFormData({ ...formData, ambiance: val })}
            label="Ambiance"
          />

          <StarRating
            rating={formData.pricing}
            setRating={(val) => setFormData({ ...formData, pricing: val })}
            label="Pricing / Value for Money"
          />

          {/* Comments */}
          <div className="mb-8 mt-8 pt-6 border-t-2 border-gray-100">
            <label className="block text-sm font-semibold text-gray-700 mb-2">
              Additional Comments / Suggestions{' '}
              <span className="text-gray-400 text-xs font-normal">(Optional)</span>
            </label>
            <textarea
              value={formData.comments}
              onChange={(e) =>
                setFormData({ ...formData, comments: e.target.value })
              }
              placeholder="Share your thoughts, suggestions, or any specific feedback..."
              rows="4"
              className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none resize-none transition-all text-gray-700 placeholder:text-gray-400"
            />
          </div>

          {/* Error Message */}
          {error && (
            <div className="mb-4 p-4 bg-red-50 border-l-4 border-red-500 text-red-700 rounded-lg text-sm flex items-start gap-3">
              <svg className="w-5 h-5 flex-shrink-0 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
              <span>{error}</span>
            </div>
          )}

          {/* Submit Button */}
          <button
            type="submit"
            disabled={submitting}
            className={`w-full py-4 px-6 rounded-xl font-bold text-lg text-white shadow-xl transition-all focus:outline-none focus:ring-4 focus:ring-offset-2 ${
              submitting
                ? 'bg-gray-300 cursor-not-allowed text-gray-500'
                : 'bg-gradient-to-r from-blue-600 via-indigo-600 to-purple-600 hover:from-blue-700 hover:via-indigo-700 hover:to-purple-700 active:scale-[0.98] focus:ring-blue-500'
            }`}
          >
            {submitting ? (
              <span className="flex items-center justify-center gap-3">
                <svg className="animate-spin h-5 w-5 text-white" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Submitting...
              </span>
            ) : (
              <span className="flex items-center justify-center gap-2">
                Submit Feedback
                <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth={2} viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M14 5l7 7m0 0l-7 7m7-7H3" />
                </svg>
              </span>
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default FeedbackForm;
