import React from 'react';

const RestaurantHeader = ({ restaurant }) => {
  const headerStyle = restaurant.image_url ? {
    backgroundImage: `url(${restaurant.image_url})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    position: 'relative'
  } : {
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
  };

  return (
    <div className="restaurant-header" style={headerStyle}>
      {/* Dark overlay for better text readability when using image background */}
      {restaurant.image_url && (
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.4)',
          zIndex: 1
        }} />
      )}
      <div className="container mx-auto px-4 py-6" style={{ maxWidth: 420, position: 'relative', zIndex: 2 }}>
        <div className="flex flex-col items-center text-center">
          {restaurant.logo_url && (
            <div className="mb-4">
              <img
                src={restaurant.logo_url}
                alt={restaurant.name}
                className="rounded-full object-cover border-4 border-white shadow-lg"
                style={{ width: 100, height: 100 }}
                onError={(e) => {
                  e.target.style.display = 'none';
                }}
              />
            </div>
          )}
          <div>
            <h1 className="font-bold text-white mb-2" style={{ fontSize: 28, lineHeight: 1.2 }}>
              {restaurant.name}
            </h1>
            {restaurant.description && (
              <p className="text-blue-100" style={{ fontSize: 15, lineHeight: 1.4 }}>
                {restaurant.description}
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RestaurantHeader;
