import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { menuAPI } from '../services/api';
import RestaurantHeader from './RestaurantHeader';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';

// A slim, read-only menu page focused purely on viewing by guests
const PublicMenu = () => {
  const { slug } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState({});
  const [categoryMeta, setCategoryMeta] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const fetchMenu = useCallback(async () => {
    try {
      setLoading(true);
      const res = await menuAPI.getMenu(slug);
      if (res.success) {
        setRestaurant(res.restaurant);
        setCategories(res.categories || {});
        setCategoryMeta(res.categoryMeta || {});
      } else {
        setError(res.message || 'Failed to load menu');
      }
    } catch (e) {
      setError(e.message || 'Failed to load menu');
    } finally {
      setLoading(false);
    }
  }, [slug]);

  useEffect(() => { fetchMenu(); }, [fetchMenu]);

  const normalizeImageUrl = (url) => {
    if (!url) return '';
    let u = String(url).trim();
    try {
      // Handle Google Images redirect links: https://www.google.com/imgres?...&imgurl=ENCODED_URL
      const parsed = new URL(u);
      if (parsed.host.includes('google.') && parsed.pathname.includes('imgres')) {
        const imgurl = parsed.searchParams.get('imgurl');
        if (imgurl) return decodeURIComponent(imgurl);
      }
      return u;
    } catch {
      return u;
    }
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;
  if (!restaurant) return <ErrorMessage message="Restaurant not found" />;

  const categoryNames = Object.keys(categories);

  return (
    <div className="min-h-screen bg-gray-100">
      <RestaurantHeader restaurant={restaurant} />

      <div className="container mx-auto px-4 py-6 max-w-md">
        {!selectedCategory ? (
          <div className="space-y-4 p-4">
            <h2 className="text-2xl font-bold text-gray-900 mb-4 text-center">Menu Categories</h2>
            <div className="grid grid-cols-1 gap-4">
              {categoryNames.map((categoryName) => {
                const imageUrl = normalizeImageUrl(categoryMeta[categoryName]?.image_url) || 'https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=800&q=80';
                const items = categories[categoryName] || [];
                return (
                  <button
                    key={categoryName}
                    onClick={() => setSelectedCategory(categoryName)}
                    className="relative cursor-pointer rounded-3xl overflow-hidden mb-5 h-48 shadow-md hover:shadow-xl transition-all"
                  >
                    <img src={imageUrl} alt={categoryName} className="absolute inset-0 w-full h-full object-cover z-0" />
                    <div className="absolute inset-0 bg-black/40 z-10" />
                    <div className="absolute inset-0 z-20 flex items-center justify-center">
                      <div className="text-center">
                        <div className="text-white text-2xl uppercase font-semibold drop-shadow-lg" style={{textShadow:'1px 1px 2px rgba(0,0,0,0.5)'}}>{categoryName}</div>
                        <div className="text-white/90 text-sm mt-1 drop-shadow-lg">{items.length} {items.length === 1 ? 'item' : 'items'}</div>
                      </div>
                    </div>
                  </button>
                );
              })}
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            <div className="flex items-center gap-3 mb-2">
              <button onClick={() => setSelectedCategory(null)} className="inline-flex items-center gap-2 px-3 py-2 rounded-full bg-gray-100 text-gray-700 hover:bg-gray-200">
                <span className="text-lg">←</span>
                <span className="font-semibold">Back</span>
              </button>
              <h2 className="text-2xl font-extrabold text-gray-900 flex-1 text-right uppercase tracking-wide">{selectedCategory}</h2>
            </div>

            <div className="space-y-3">
              {(categories[selectedCategory] || []).map((item) => (
                <div key={item.id} className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden">
                  <div className="flex gap-4 p-4">
                    {item.image_url ? (
                      <img src={normalizeImageUrl(item.image_url)} alt={item.name} className="w-20 h-20 rounded-lg object-cover flex-shrink-0" loading="lazy" />
                    ) : (
                      <div className="w-20 h-20 rounded-lg bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center flex-shrink-0">
                        <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                        </svg>
                      </div>
                    )}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-start justify-between gap-2 mb-1">
                        <h3 className="text-base font-semibold text-gray-900">{item.name}</h3>
                        <span className="text-lg font-bold text-green-600 whitespace-nowrap">₹{parseFloat(item.price).toFixed(2)}</span>
                      </div>
                      {item.description && (<p className="text-sm text-gray-600 line-clamp-2">{item.description}</p>)}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PublicMenu;
