import React from 'react';

const RestaurantInfo = ({ restaurant }) => {
  if (!restaurant) return null;

  const { address, phone, wifi_name, wifi_password, custom_sections } = restaurant;

  return (
    <div style={{ 
      background: '#fff', 
      padding: '20px',
      marginTop: '0'
    }}>
      <div className="container mx-auto px-4" style={{ maxWidth: 420 }}>
        {/* Restaurant Name */}
        <h2 style={{ 
          margin: '0 0 16px 0', 
          fontSize: '24px', 
          fontWeight: '700', 
          color: '#1a202c',
          textAlign: 'left'
        }}>
          {restaurant.name}
        </h2>
        
        {/* Address */}
        {address && (
          <div style={{ 
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            marginBottom: '8px'
          }}>
            <span style={{ color: '#6b7280', fontSize: '16px' }}>üìç</span>
            <span style={{ 
              fontSize: '14px', 
              color: '#6b7280'
            }}>
              {address}
            </span>
          </div>
        )}

        {/* Phone */}
        {phone && (
          <div style={{ 
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            marginBottom: '8px'
          }}>
            <span style={{ color: '#6b7280', fontSize: '16px' }}>üìû</span>
            <a 
              href={`tel:${phone}`} 
              style={{ 
                fontSize: '14px', 
                color: '#6b7280', 
                textDecoration: 'none'
              }}
            >
              {phone}
            </a>
          </div>
        )}

        {/* WiFi */}
        {(wifi_name || wifi_password) && (
          <div style={{ 
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            marginBottom: '8px'
          }}>
            <span style={{ color: '#6b7280', fontSize: '16px' }}>üì∂</span>
            <span style={{ 
              fontSize: '14px', 
              color: '#6b7280'
            }}>
              {wifi_name && wifi_password ? `${wifi_name}_${wifi_password}` : wifi_name || wifi_password}
            </span>
          </div>
        )}

        {/* Custom Sections */}
        {custom_sections && Array.isArray(custom_sections) && custom_sections.length > 0 && (
          <div style={{ marginTop: '12px' }}>
            {custom_sections.map((section, index) => (
              <div key={index} style={{ marginBottom: '8px' }}>
                <div style={{ 
                  fontSize: '14px', 
                  fontWeight: '600', 
                  color: '#374151',
                  marginBottom: '4px'
                }}>
                  {section.title}
                </div>
                <div style={{ 
                  fontSize: '14px', 
                  color: '#6b7280',
                  lineHeight: '1.4'
                }}>
                  {section.content}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantInfo;
