import React, { useState } from 'react';
import axios from 'axios';

const StarRating = ({ rating, setRating, label, required = true }) => {
  const [hover, setHover] = useState(0);

  const getLabel = (val) => {
    if (val === 1) return 'Poor';
    if (val === 2) return 'Fair';
    if (val === 3) return 'Good';
    if (val === 4) return 'Very Good';
    if (val === 5) return 'Excellent';
    return '';
  };

  const current = hover || rating;

  return (
    <div className="mb-5">
      <label className="block text-sm font-semibold text-gray-800 mb-2 select-none">
        {label} {required && <span className="text-red-500">*</span>}
      </label>
      <div className="flex items-center gap-2">
        {[1, 2, 3, 4, 5].map((star) => {
          const active = star <= current;
          return (
            <button
              key={star}
              type="button"
              aria-label={`${star} star${star > 1 ? 's' : ''}`}
              onClick={() => setRating(star)}
              onMouseEnter={() => setHover(star)}
              onMouseLeave={() => setHover(0)}
              className={`w-8 h-8 rounded-md transition-transform focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${active ? 'scale-110' : 'hover:scale-105'}`}
            >
              <svg viewBox="0 0 24 24" className={`w-8 h-8 ${active ? 'text-yellow-500' : 'text-gray-300'}`}>
                {/* Heroicon Star */}
                <path
                  d="M11.48 3.5c.2-.4.84-.4 1.04 0l2.07 4.2c.12.24.35.41.62.45l4.64.67c.45.07.63.62.3.93l-3.36 3.27c-.2.2-.3.48-.25.76l.79 4.6c.08.45-.4.8-.81.58l-4.13-2.17a.86.86 0 00-.8 0l-4.13 2.17c-.41.22-.89-.13-.81-.58l.79-4.6a.86.86 0 00-.25-.76L3.85 9.75a.62.62 0 01.3-.93l4.64-.67c.27-.04.5-.21.62-.45l2.07-4.2z"
                  fill={active ? 'currentColor' : 'none'}
                  stroke="currentColor"
                  strokeWidth="1.5"
                />
              </svg>
            </button>
          );
        })}
        <span className="ml-2 text-xs font-medium text-gray-500 min-w-[70px]">
          {getLabel(current)}
        </span>
      </div>
    </div>
  );
};

const FeedbackForm = ({ restaurantId, restaurantName, onClose }) => {
  const [formData, setFormData] = useState({
    customer_name: '',
    food_quality: 0,
    service: 0,
    ambiance: 0,
    pricing: 0,
    comments: ''
  });

  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate all ratings are provided
    if (
      !formData.food_quality ||
      !formData.service ||
      !formData.ambiance ||
      !formData.pricing
    ) {
      setError('Please provide all ratings');
      return;
    }

    setSubmitting(true);

    try {
      const response = await axios.post('/api/feedback/submit', {
        restaurant_id: restaurantId,
        ...formData
      });

      if (response.data.success) {
        setSubmitted(true);
        setTimeout(() => {
          onClose && onClose();
        }, 2000);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit feedback');
    } finally {
      setSubmitting(false);
    }
  };

  if (submitted) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-2xl shadow-xl max-w-md w-full p-8 text-center">
          <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <svg
              className="w-10 h-10 text-green-500"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M5 13l4 4L19 7"
              />
            </svg>
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-2">
            Thank You!
          </h3>
          <p className="text-gray-600">
            Your feedback has been submitted successfully. We appreciate your time!
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50 overflow-y-auto">
      <div className="bg-white rounded-2xl shadow-2xl max-w-lg w-full my-8 border border-gray-100">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white px-6 py-4 rounded-t-2xl">
          <div className="flex justify-between items-center">
            <h2 className="text-2xl font-bold leading-none">Share Your Experience</h2>
            <button
              onClick={onClose}
              className="text-white/90 hover:text-white text-2xl leading-none px-2 py-1 rounded-md hover:bg-white/10 focus:outline-none focus:ring-2 focus:ring-white/40"
            >
              Ã—
            </button>
          </div>
          <p className="text-blue-100 text-sm mt-1">{restaurantName}</p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6">
          {/* Customer Name (Optional) */}
          <div className="mb-4">
            <label className="block text-sm font-semibold text-gray-700 mb-2">
              Your Name <span className="text-gray-400 text-xs">(Optional)</span>
            </label>
            <input
              type="text"
              value={formData.customer_name}
              onChange={(e) =>
                setFormData({ ...formData, customer_name: e.target.value })
              }
              placeholder="Enter your name"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none"
            />
          </div>

          {/* Star Ratings */}
          <StarRating
            rating={formData.food_quality}
            setRating={(val) => setFormData({ ...formData, food_quality: val })}
            label="Food Quality"
          />

          <StarRating
            rating={formData.service}
            setRating={(val) => setFormData({ ...formData, service: val })}
            label="Service"
          />

          <StarRating
            rating={formData.ambiance}
            setRating={(val) => setFormData({ ...formData, ambiance: val })}
            label="Ambiance"
          />

          <StarRating
            rating={formData.pricing}
            setRating={(val) => setFormData({ ...formData, pricing: val })}
            label="Pricing / Value for Money"
          />

          {/* Comments */}
          <div className="mb-6">
            <label className="block text-sm font-semibold text-gray-700 mb-2">
              Additional Comments / Suggestions{' '}
              <span className="text-gray-400 text-xs">(Optional)</span>
            </label>
            <textarea
              value={formData.comments}
              onChange={(e) =>
                setFormData({ ...formData, comments: e.target.value })
              }
              placeholder="Share your thoughts..."
              rows="4"
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none resize-none"
            />
          </div>

          {/* Error Message */}
          {error && (
            <div className="mb-4 p-3 bg-red-50 border border-red-200 text-red-700 rounded-lg text-sm">
              {error}
            </div>
          )}

          {/* Submit Button */}
          <button
            type="submit"
            disabled={submitting}
            className={`w-full py-3 px-6 rounded-xl font-semibold text-white shadow-lg transition-all focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
              submitting
                ? 'bg-gray-300 cursor-not-allowed text-gray-600'
                : 'bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 active:scale-[0.99]'
            }`}
          >
            {submitting ? 'Submitting...' : 'Submit Feedback'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default FeedbackForm;
