import React, { useState } from 'react';
import axios from 'axios';

const StarRating = ({ rating, setRating, label, required = true }) => {
  const [hover, setHover] = useState(0);

  const getRatingLabel = (value) => {
    const labels = {
      1: 'Poor',
      2: 'Fair',
      3: 'Good',
      4: 'Very Good',
      5: 'Excellent'
    };
    return labels[value] || '';
  };

  const current = hover || rating;

  return (
    <div className="mb-4">
      <div className="flex items-center justify-between mb-2">
        <label className="text-sm font-semibold text-gray-700">
          {label} {required && <span className="text-red-500">*</span>}
        </label>
        {current > 0 && (
          <span className="text-xs font-medium text-amber-600 px-2 py-1 bg-amber-50 rounded-full">
            {getRatingLabel(current)}
          </span>
        )}
      </div>
      <div className="flex items-center gap-2">
        {[1, 2, 3, 4, 5].map((star) => {
          const isActive = star <= current;
          return (
            <button
              key={star}
              type="button"
              aria-label={`${star} star${star > 1 ? 's' : ''}`}
              onClick={() => setRating(star)}
              onMouseEnter={() => setHover(star)}
              onMouseLeave={() => setHover(0)}
              className={`transition-all duration-200 ease-in-out focus:outline-none focus:scale-110 ${
                isActive ? 'scale-110' : 'hover:scale-105'
              }`}
            >
              <svg 
                width="32" 
                height="32" 
                viewBox="0 0 24 24" 
                fill={isActive ? '#FFD700' : 'none'}
                stroke={isActive ? '#FFD700' : '#e4e5e9'}
                strokeWidth="1.5"
                strokeLinecap="round"
                strokeLinejoin="round"
                className="transition-all duration-200"
                style={{ filter: isActive ? 'drop-shadow(0 2px 4px rgba(255, 215, 0, 0.4))' : 'none' }}
              >
                <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
              </svg>
            </button>
          );
        })}
      </div>
    </div>
  );
};

const FeedbackForm = ({ restaurantId, restaurantName, onClose }) => {
  const [formData, setFormData] = useState({
    customer_name: '',
    food_quality: 0,
    service: 0,
    ambiance: 0,
    pricing: 0,
    comments: ''
  });

  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate all ratings are provided
    if (
      !formData.food_quality ||
      !formData.service ||
      !formData.ambiance ||
      !formData.pricing
    ) {
      setError('Please provide all ratings');
      return;
    }

    setSubmitting(true);

    try {
      const response = await axios.post('/api/feedback/submit', {
        restaurant_id: restaurantId,
        ...formData
      });

      if (response.data.success) {
        setSubmitted(true);
        setTimeout(() => {
          onClose && onClose();
        }, 2000);
      }
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to submit feedback');
    } finally {
      setSubmitting(false);
    }
  };

  if (submitted) {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center p-4 z-50 animate-fade-in">
        <div className="bg-white rounded-2xl shadow-2xl max-w-md w-full p-8 text-center animate-scale-in">
          <div className="w-16 h-16 bg-gradient-to-br from-green-400 to-emerald-500 rounded-full flex items-center justify-center mx-auto mb-4 shadow-lg">
            <svg
              className="w-9 h-9 text-white"
              fill="none"
              stroke="currentColor"
              strokeWidth={3}
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M5 13l4 4L19 7"
              />
            </svg>
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-2">
            Thank You!
          </h3>
          <p className="text-gray-600">
            Your feedback has been submitted successfully. We appreciate your time!
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-50 overflow-y-auto animate-fade-in">
      <div className="bg-white rounded-xl shadow-2xl max-w-lg w-full my-8 animate-slide-up">
        {/* Header */}
        <div className="relative bg-white border-b border-gray-200 px-6 py-4 rounded-t-xl flex items-center justify-between">
          <div>
            <h2 className="text-xl font-bold text-gray-900">Share Your Experience</h2>
            <p className="text-gray-600 text-sm mt-1">{restaurantName}</p>
          </div>
          <button
            type="button"
            onClick={onClose}
            className="w-10 h-10 rounded-lg bg-red-500 hover:bg-red-600 flex items-center justify-center transition-all focus:outline-none focus:ring-2 focus:ring-red-300 flex-shrink-0"
            aria-label="Close feedback form"
          >
            <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" strokeWidth="2.5" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="p-6 space-y-5">
          {/* Customer Name */}
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-2">
              Your Name <span className="text-gray-400 text-xs font-normal">(Optional)</span>
            </label>
            <input
              type="text"
              value={formData.customer_name}
              onChange={(e) =>
                setFormData({ ...formData, customer_name: e.target.value })
              }
              placeholder="Enter your name"
              className="w-full px-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all text-gray-700 placeholder:text-gray-400"
            />
          </div>

          {/* Divider */}
          <div className="border-t border-gray-200 pt-4">
            <h3 className="text-base font-bold text-gray-800 mb-3">Rate Your Experience</h3>
            <p className="text-xs text-gray-500 mb-4">Please rate the following aspects</p>
          </div>

          {/* Star Ratings */}
          <StarRating
            rating={formData.food_quality}
            setRating={(val) => setFormData({ ...formData, food_quality: val })}
            label="Food Quality"
          />

          <StarRating
            rating={formData.service}
            setRating={(val) => setFormData({ ...formData, service: val })}
            label="Service"
          />

          <StarRating
            rating={formData.ambiance}
            setRating={(val) => setFormData({ ...formData, ambiance: val })}
            label="Ambiance"
          />

          <StarRating
            rating={formData.pricing}
            setRating={(val) => setFormData({ ...formData, pricing: val })}
            label="Pricing / Value for Money"
          />

          {/* Comments */}
          <div className="pt-4 border-t border-gray-200">
            <label className="block text-sm font-semibold text-gray-700 mb-2">
              Additional Comments / Suggestions{' '}
              <span className="text-gray-400 text-xs font-normal">(Optional)</span>
            </label>
            <textarea
              value={formData.comments}
              onChange={(e) =>
                setFormData({ ...formData, comments: e.target.value })
              }
              placeholder="Share your thoughts, suggestions, or any specific feedback..."
              rows="4"
              className="w-full px-4 py-2.5 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none resize-none transition-all text-gray-700 placeholder:text-gray-400"
            />
          </div>

          {/* Error Message */}
          {error && (
            <div className="p-3 bg-red-50 border-l-4 border-red-500 text-red-700 rounded text-sm flex items-start gap-2">
              <svg className="w-4 h-4 flex-shrink-0 mt-0.5" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
              <span>{error}</span>
            </div>
          )}

          {/* Submit Button */}
          <div className="flex justify-center">
            <button
              type="submit"
              disabled={submitting}
              className={`px-12 py-2.5 rounded-full font-medium text-base text-white shadow-md transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
                submitting
                  ? 'bg-gray-400 cursor-not-allowed opacity-60'
                  : 'bg-blue-600 hover:bg-blue-700 hover:shadow-lg active:scale-95'
              }`}
            >
              {submitting ? (
                <span className="flex items-center justify-center gap-2">
                  <svg className="animate-spin h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  <span>Submitting...</span>
                </span>
              ) : (
                'Submit'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FeedbackForm;

