import React from 'react';

const RestaurantInfo = ({ restaurant }) => {
  if (!restaurant) return null;

  const { address, phone, wifi_name, wifi_password, custom_sections } = restaurant;

  return (
    <div style={{ 
      background: '#f8fafc', 
      padding: '24px 0',
      marginTop: '0'
    }}>
      <div className="container mx-auto px-4" style={{ maxWidth: 420 }}>
        {/* Restaurant Information Header */}
        <div style={{ 
          background: '#fff', 
          borderRadius: '16px', 
          boxShadow: '0 4px 20px rgba(0,0,0,0.08)', 
          border: '1px solid #e2e8f0', 
          padding: '24px',
          marginBottom: '20px'
        }}>
          <h3 style={{ 
            margin: '0 0 20px 0', 
            fontSize: '18px', 
            fontWeight: '700', 
            color: '#1a202c',
            textAlign: 'center',
            letterSpacing: '0.5px'
          }}>
            Restaurant Information
          </h3>
          
          <div style={{ display: 'flex', flexDirection: 'column', gap: '16px' }}>
            {/* Address Section */}
            {address && (
              <div style={{ 
                display: 'flex',
                alignItems: 'flex-start',
                gap: '16px',
                padding: '16px',
                background: '#f7fafc',
                borderRadius: '12px',
                border: '1px solid #e2e8f0'
              }}>
                <div style={{ 
                  width: '40px', 
                  height: '40px', 
                  background: '#fef2f2', 
                  borderRadius: '10px', 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  flexShrink: 0
                }}>
                  <span style={{ color: '#dc2626', fontSize: '20px' }}>üìç</span>
                </div>
                <div style={{ flex: 1 }}>
                  <h4 style={{ 
                    margin: '0 0 4px 0', 
                    fontSize: '14px', 
                    fontWeight: '600', 
                    color: '#374151',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    Address
                  </h4>
                  <p style={{ 
                    margin: '0', 
                    fontSize: '15px', 
                    color: '#6b7280',
                    lineHeight: '1.4'
                  }}>
                    {address}
                  </p>
                </div>
              </div>
            )}

            {/* Phone Number Section */}
            {phone && (
              <div style={{ 
                display: 'flex',
                alignItems: 'center',
                gap: '16px',
                padding: '16px',
                background: '#f7fafc',
                borderRadius: '12px',
                border: '1px solid #e2e8f0'
              }}>
                <div style={{ 
                  width: '40px', 
                  height: '40px', 
                  background: '#f0f9ff', 
                  borderRadius: '10px', 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  flexShrink: 0
                }}>
                  <span style={{ color: '#0284c7', fontSize: '20px' }}>üìû</span>
                </div>
                <div style={{ flex: 1 }}>
                  <h4 style={{ 
                    margin: '0 0 4px 0', 
                    fontSize: '14px', 
                    fontWeight: '600', 
                    color: '#374151',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    Phone
                  </h4>
                  <a 
                    href={`tel:${phone}`} 
                    style={{ 
                      margin: '0', 
                      fontSize: '15px', 
                      color: '#2563eb', 
                      textDecoration: 'none',
                      fontWeight: '500'
                    }}
                  >
                    {phone}
                  </a>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* WiFi Information Section */}
        {(wifi_name || wifi_password) && (
          <div style={{ 
            background: '#eff6ff', 
            borderRadius: '16px', 
            boxShadow: '0 4px 20px rgba(59, 130, 246, 0.15)', 
            border: '1px solid #bfdbfe', 
            padding: '20px',
            marginBottom: '20px'
          }}>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '12px', 
              marginBottom: '16px' 
            }}>
              <div style={{ 
                width: '40px', 
                height: '40px', 
                background: '#dbeafe', 
                borderRadius: '10px', 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center'
              }}>
                <span style={{ color: '#2563eb', fontSize: '20px' }}>üì∂</span>
              </div>
              <h4 style={{ 
                margin: 0, 
                fontSize: '16px', 
                fontWeight: '700', 
                color: '#1e40af',
                textTransform: 'uppercase',
                letterSpacing: '0.5px'
              }}>
                Free WiFi Available
              </h4>
            </div>
            <div style={{ paddingLeft: '52px' }}>
              {wifi_name && (
                <p style={{ 
                  margin: '0 0 8px 0', 
                  fontSize: '14px', 
                  color: '#1e40af',
                  fontWeight: '500'
                }}>
                  <strong>Network:</strong> {wifi_name}
                </p>
              )}
              {wifi_password && (
                <p style={{ 
                  margin: '0', 
                  fontSize: '14px', 
                  color: '#1e40af',
                  fontWeight: '500'
                }}>
                  <strong>Password:</strong> {wifi_password}
                </p>
              )}
            </div>
          </div>
        )}

        {/* Custom Sections */}
        {custom_sections && Array.isArray(custom_sections) && custom_sections.length > 0 && (
          <div style={{ 
            background: '#fffbeb', 
            borderRadius: '16px', 
            boxShadow: '0 4px 20px rgba(245, 158, 11, 0.15)', 
            border: '1px solid #fcd34d', 
            padding: '20px',
            marginBottom: '20px'
          }}>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '12px', 
              marginBottom: '16px' 
            }}>
              <div style={{ 
                width: '40px', 
                height: '40px', 
                background: '#fef3c7', 
                borderRadius: '10px', 
                display: 'flex', 
                alignItems: 'center', 
                justifyContent: 'center'
              }}>
                <span style={{ color: '#d97706', fontSize: '20px' }}>‚ÑπÔ∏è</span>
              </div>
              <h4 style={{ 
                margin: 0, 
                fontSize: '16px', 
                fontWeight: '700', 
                color: '#92400e',
                textTransform: 'uppercase',
                letterSpacing: '0.5px'
              }}>
                Additional Information
              </h4>
            </div>
            <div style={{ paddingLeft: '52px' }}>
              {custom_sections.map((section, index) => (
                <div 
                  key={index} 
                  style={{ 
                    marginBottom: index < custom_sections.length - 1 ? '16px' : '0', 
                    paddingBottom: index < custom_sections.length - 1 ? '16px' : '0',
                    borderBottom: index < custom_sections.length - 1 ? '1px dashed #fde68a' : 'none'
                  }}
                >
                  <p style={{ 
                    margin: '0 0 4px 0', 
                    fontSize: '14px', 
                    fontWeight: '600', 
                    color: '#d97706',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    {section.title}
                  </p>
                  <p style={{ 
                    margin: '0', 
                    fontSize: '14px', 
                    color: '#b45309',
                    lineHeight: '1.4'
                  }}>
                    {section.content}
                  </p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantInfo;
