import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { menuAPI } from '../services/api';
import RestaurantHeader from './RestaurantHeader';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';

const MenuPage = () => {
  const { slug } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [expandedCategories, setExpandedCategories] = useState({});

  useEffect(() => {
    fetchMenuData();
  }, [slug]);

  useEffect(() => {
    // Expand all categories by default
    const expanded = {};
    Object.keys(categories).forEach(cat => {
      expanded[cat] = true;
    });
    setExpandedCategories(expanded);
  }, [categories]);

  const fetchMenuData = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await menuAPI.getMenu(slug);
      
      if (response.success) {
        setRestaurant(response.restaurant);
        setCategories(response.categories);
      } else {
        setError(response.message || 'Failed to load menu');
      }
    } catch (err) {
      setError(err.message || 'Failed to load menu');
    } finally {
      setLoading(false);
    }
  };

  const toggleCategory = (categoryName) => {
    setExpandedCategories(prev => ({
      ...prev,
      [categoryName]: !prev[categoryName]
    }));
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} />;
  }

  if (!restaurant) {
    return <ErrorMessage message="Restaurant not found" />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
      <RestaurantHeader restaurant={restaurant} />
      
      <div className="container mx-auto px-4 py-8 max-w-5xl">
        {Object.keys(categories).length === 0 ? (
          <div className="text-center py-16 bg-white rounded-2xl shadow-sm">
            <div className="text-gray-400 mb-4">
              <svg className="w-20 h-20 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
              </svg>
            </div>
            <p className="text-gray-500 text-lg font-medium">No menu items available yet</p>
          </div>
        ) : (
          <div className="space-y-6">
            {Object.entries(categories).map(([categoryName, items]) => (
              <div 
                key={categoryName} 
                className="bg-white rounded-2xl shadow-sm border border-gray-200 overflow-hidden transition-all hover:shadow-md"
              >
                {/* Category Header */}
                <button
                  onClick={() => toggleCategory(categoryName)}
                  className="w-full px-6 py-5 flex items-center justify-between bg-gradient-to-r from-indigo-50 to-purple-50 hover:from-indigo-100 hover:to-purple-100 transition-all"
                >
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-full bg-white shadow-sm flex items-center justify-center">
                      <span className="text-xl">🍽️</span>
                    </div>
                    <div className="text-left">
                      <h2 className="text-xl font-bold text-gray-900">{categoryName}</h2>
                      <p className="text-sm text-gray-600">{items.length} {items.length === 1 ? 'item' : 'items'}</p>
                    </div>
                  </div>
                  <svg 
                    className={`w-6 h-6 text-gray-600 transition-transform duration-300 ${expandedCategories[categoryName] ? 'rotate-180' : ''}`}
                    fill="none" 
                    stroke="currentColor" 
                    viewBox="0 0 24 24"
                  >
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M19 9l-7 7-7-7" />
                  </svg>
                </button>

                {/* Category Items */}
                {expandedCategories[categoryName] && (
                  <div className="divide-y divide-gray-100">
                    {items.map((item) => (
                      <MenuItem key={item.id} item={item} />
                    ))}
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
      
      <footer className="bg-white border-t border-gray-200 mt-16">
        <div className="container mx-auto px-4 py-6">
          <div className="text-center">
            <p className="text-sm text-gray-500">
              Powered by <span className="font-semibold text-indigo-600">SmartMenu</span>
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

// MenuItem Component - Compact horizontal layout
const MenuItem = ({ item }) => {
  const [imageError, setImageError] = useState(false);
  const imageUrl = item.image_url || '';
  const hasImage = imageUrl && !imageError;

  return (
    <div className="px-6 py-5 hover:bg-gray-50 transition-colors">
      <div className="flex gap-4">
        {/* Image */}
        {hasImage ? (
          <img
            src={imageUrl}
            alt={item.name}
            className="w-24 h-24 rounded-xl object-cover flex-shrink-0 shadow-sm"
            onError={() => setImageError(true)}
            loading="lazy"
          />
        ) : (
          <div className="w-24 h-24 rounded-xl bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center flex-shrink-0 shadow-sm">
            <svg className="w-10 h-10 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
            </svg>
          </div>
        )}

        {/* Content */}
        <div className="flex-1 min-w-0">
          <div className="flex items-start justify-between gap-3 mb-2">
            <h3 className="text-lg font-semibold text-gray-900 leading-tight">{item.name}</h3>
            <span className="text-xl font-bold text-green-600 whitespace-nowrap">₹{parseFloat(item.price).toFixed(2)}</span>
          </div>
          
          {item.description && (
            <p className="text-sm text-gray-600 leading-relaxed line-clamp-2">
              {item.description}
            </p>
          )}

          {!item.is_available && (
            <div className="mt-2">
              <span className="inline-flex items-center px-2.5 py-1 rounded-full text-xs font-semibold bg-red-100 text-red-700">
                Currently Unavailable
              </span>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default MenuPage;
