import React from 'react';

const RestaurantInfo = ({ restaurant }) => {
  if (!restaurant) return null;

  const { address, phone, wifi_name, wifi_password, custom_sections } = restaurant;

  return (
    <div style={{ 
      background: '#f8fafc', 
      padding: '16px 0',
      marginTop: '0'
    }}>
      <div className="container mx-auto px-4" style={{ maxWidth: 420 }}>
        {/* Restaurant Information - Compact Layout */}
        <div style={{ 
          background: '#fff', 
          borderRadius: '12px', 
          boxShadow: '0 2px 8px rgba(0,0,0,0.06)', 
          border: '1px solid #e2e8f0', 
          padding: '16px',
          marginBottom: '12px'
        }}>
          <h3 style={{ 
            margin: '0 0 12px 0', 
            fontSize: '16px', 
            fontWeight: '600', 
            color: '#1a202c',
            textAlign: 'center'
          }}>
            Restaurant Information
          </h3>
          
          <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
            {/* Address Section */}
            {address && (
              <div style={{ 
                display: 'flex',
                alignItems: 'center',
                gap: '12px',
                padding: '8px 12px',
                background: '#f7fafc',
                borderRadius: '8px',
                border: '1px solid #e2e8f0'
              }}>
                <span style={{ color: '#dc2626', fontSize: '16px' }}>üìç</span>
                <div style={{ flex: 1 }}>
                  <span style={{ 
                    fontSize: '12px', 
                    fontWeight: '600', 
                    color: '#374151',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    Address: 
                  </span>
                  <span style={{ 
                    fontSize: '13px', 
                    color: '#6b7280',
                    marginLeft: '4px'
                  }}>
                    {address}
                  </span>
                </div>
              </div>
            )}

            {/* Phone Number Section */}
            {phone && (
              <div style={{ 
                display: 'flex',
                alignItems: 'center',
                gap: '12px',
                padding: '8px 12px',
                background: '#f7fafc',
                borderRadius: '8px',
                border: '1px solid #e2e8f0'
              }}>
                <span style={{ color: '#0284c7', fontSize: '16px' }}>üìû</span>
                <div style={{ flex: 1 }}>
                  <span style={{ 
                    fontSize: '12px', 
                    fontWeight: '600', 
                    color: '#374151',
                    textTransform: 'uppercase',
                    letterSpacing: '0.5px'
                  }}>
                    Phone: 
                  </span>
                  <a 
                    href={`tel:${phone}`} 
                    style={{ 
                      fontSize: '13px', 
                      color: '#2563eb', 
                      textDecoration: 'none',
                      marginLeft: '4px'
                    }}
                  >
                    {phone}
                  </a>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* WiFi Information Section - Compact */}
        {(wifi_name || wifi_password) && (
          <div style={{ 
            background: '#eff6ff', 
            borderRadius: '12px', 
            boxShadow: '0 2px 8px rgba(59, 130, 246, 0.1)', 
            border: '1px solid #bfdbfe', 
            padding: '12px',
            marginBottom: '12px'
          }}>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px', 
              marginBottom: '8px' 
            }}>
              <span style={{ color: '#2563eb', fontSize: '16px' }}>üì∂</span>
              <span style={{ 
                fontSize: '14px', 
                fontWeight: '600', 
                color: '#1e40af'
              }}>
                Free WiFi Available
              </span>
            </div>
            <div style={{ paddingLeft: '24px' }}>
              {wifi_name && (
                <div style={{ 
                  margin: '0 0 4px 0', 
                  fontSize: '12px', 
                  color: '#1e40af'
                }}>
                  <strong>Network:</strong> {wifi_name}
                </div>
              )}
              {wifi_password && (
                <div style={{ 
                  margin: '0', 
                  fontSize: '12px', 
                  color: '#1e40af'
                }}>
                  <strong>Password:</strong> {wifi_password}
                </div>
              )}
            </div>
          </div>
        )}

        {/* Custom Sections - Compact */}
        {custom_sections && Array.isArray(custom_sections) && custom_sections.length > 0 && (
          <div style={{ 
            background: '#fffbeb', 
            borderRadius: '12px', 
            boxShadow: '0 2px 8px rgba(245, 158, 11, 0.1)', 
            border: '1px solid #fcd34d', 
            padding: '12px',
            marginBottom: '12px'
          }}>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px', 
              marginBottom: '8px' 
            }}>
              <span style={{ color: '#d97706', fontSize: '16px' }}>‚ÑπÔ∏è</span>
              <span style={{ 
                fontSize: '14px', 
                fontWeight: '600', 
                color: '#92400e'
              }}>
                Additional Information
              </span>
            </div>
            <div style={{ paddingLeft: '24px' }}>
              {custom_sections.map((section, index) => (
                <div 
                  key={index} 
                  style={{ 
                    marginBottom: index < custom_sections.length - 1 ? '8px' : '0', 
                    paddingBottom: index < custom_sections.length - 1 ? '8px' : '0',
                    borderBottom: index < custom_sections.length - 1 ? '1px dashed #fde68a' : 'none'
                  }}
                >
                  <div style={{ 
                    margin: '0 0 2px 0', 
                    fontSize: '12px', 
                    fontWeight: '600', 
                    color: '#d97706'
                  }}>
                    {section.title}
                  </div>
                  <div style={{ 
                    margin: '0', 
                    fontSize: '12px', 
                    color: '#b45309',
                    lineHeight: '1.3'
                  }}>
                    {section.content}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantInfo;
