import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { menuAPI } from '../services/api';
import RestaurantHeader from './RestaurantHeader';
import RestaurantInfo from './RestaurantInfo';
import LoadingSpinner from './LoadingSpinner';
import ErrorMessage from './ErrorMessage';

// A slim, read-only menu page focused purely on viewing by guests
const PublicMenu = () => {
  const { slug } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [categories, setCategories] = useState({});
  const [categoryMeta, setCategoryMeta] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [isSearching, setIsSearching] = useState(false);

  const fetchMenu = useCallback(async () => {
    try {
      setLoading(true);
      const res = await menuAPI.getMenu(slug);
      if (res.success) {
        setRestaurant(res.restaurant);
        setCategories(res.categories || {});
        setCategoryMeta(res.categoryMeta || {});
      } else {
        setError(res.message || 'Failed to load menu');
      }
    } catch (e) {
      setError(e.message || 'Failed to load menu');
    } finally {
      setLoading(false);
    }
  }, [slug]);

  useEffect(() => { fetchMenu(); }, [fetchMenu]);

  // Search functionality
  const handleSearch = useCallback(async (term) => {
    if (!term || term.trim() === '') {
      setSearchResults(null);
      return;
    }

    try {
      setIsSearching(true);
      const res = await menuAPI.searchMenu(slug, term);
      if (res.success) {
        setSearchResults(res);
      } else {
        setError(res.message || 'Search failed');
      }
    } catch (e) {
      setError(e.message || 'Search failed');
    } finally {
      setIsSearching(false);
    }
  }, [slug]);

  // Debounced search
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      if (searchTerm.trim()) {
        handleSearch(searchTerm);
      } else {
        setSearchResults(null);
      }
    }, 300);

    return () => clearTimeout(timeoutId);
  }, [searchTerm, handleSearch]);

  const clearSearch = () => {
    setSearchTerm('');
    setSearchResults(null);
  };

  const normalizeImageUrl = (url) => {
    if (!url) return '';
    let u = String(url).trim();
    try {
      // Handle Google Images redirect links: https://www.google.com/imgres?...&imgurl=ENCODED_URL
      const parsed = new URL(u);
      if (parsed.host.includes('google.') && parsed.pathname.includes('imgres')) {
        const imgurl = parsed.searchParams.get('imgurl');
        if (imgurl) return decodeURIComponent(imgurl);
      }
      return u;
    } catch {
      return u;
    }
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;
  if (!restaurant) return <ErrorMessage message="Restaurant not found" />;

  const categoryNames = Object.keys(categories);

  return (
    <div className="min-h-screen" style={{ background:'#f7f7f7' }}>
      <RestaurantHeader restaurant={restaurant} />

      <div className="container mx-auto px-4 py-6" style={{ maxWidth: 420 }}>
        {/* Restaurant Information Section */}
        <RestaurantInfo restaurant={restaurant} />

        {!selectedCategory ? (
          <div style={{ padding:'16px' }}>
            <h2 style={{ fontSize:22, fontWeight:800, color:'#111827', textAlign:'center', marginBottom:16 }}>Menu Categories</h2>

            {/* Universal Search Bar */}
            <div style={{ marginBottom:20, position:'relative' }}>
              <div style={{ 
                display:'flex', 
                alignItems:'center', 
                background:'#fff', 
                borderRadius:25, 
                padding:'12px 16px', 
                boxShadow:'0 2px 8px rgba(0,0,0,0.1)',
                border:'1px solid #e5e7eb'
              }}>
                <svg style={{ width:20, height:20, color:'#6b7280', marginRight:12 }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
                <input
                  type="text"
                  placeholder="Search all menu items..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  style={{
                    flex:1,
                    border:'none',
                    outline:'none',
                    fontSize:16,
                    color:'#374151',
                    background:'transparent'
                  }}
                />
                {searchTerm && (
                  <button
                    onClick={clearSearch}
                    style={{
                      background:'none',
                      border:'none',
                      color:'#6b7280',
                      cursor:'pointer',
                      padding:4,
                      marginLeft:8
                    }}
                  >
                    <svg style={{ width:18, height:18 }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                )}
              </div>
              {isSearching && (
                <div style={{ 
                  position:'absolute', 
                  top:'100%', 
                  left:0, 
                  right:0, 
                  background:'#fff', 
                  borderRadius:12, 
                  padding:12, 
                  marginTop:8, 
                  boxShadow:'0 4px 12px rgba(0,0,0,0.15)',
                  textAlign:'center',
                  color:'#6b7280'
                }}>
                  Searching...
                </div>
              )}
            </div>

            {/* Search Results or Categories */}
            {searchResults ? (
              <div>
                <div style={{ 
                  display:'flex', 
                  alignItems:'center', 
                  justifyContent:'space-between', 
                  marginBottom:16 
                }}>
                  <h3 style={{ fontSize:18, fontWeight:600, color:'#111827' }}>
                    Search Results ({searchResults.totalResults} items)
                  </h3>
                  <button
                    onClick={clearSearch}
                    style={{
                      background:'#f3f4f6',
                      border:'none',
                      borderRadius:8,
                      padding:'6px 12px',
                      color:'#374151',
                      cursor:'pointer',
                      fontSize:14
                    }}
                  >
                    Clear Search
                  </button>
                </div>

                {Object.keys(searchResults.categories).length === 0 ? (
                  <div style={{ 
                    textAlign:'center', 
                    padding:40, 
                    background:'#fff', 
                    borderRadius:12, 
                    color:'#6b7280' 
                  }}>
                    No items found for "{searchResults.searchTerm}"
                  </div>
                ) : (
                  <div style={{ display:'grid', gap:16 }}>
                    {Object.entries(searchResults.categories).map(([categoryName, items]) => (
                      <div key={categoryName} style={{ 
                        background:'#fff', 
                        borderRadius:12, 
                        padding:16, 
                        boxShadow:'0 2px 8px rgba(0,0,0,0.1)' 
                      }}>
                        <h4 style={{ 
                          fontSize:16, 
                          fontWeight:600, 
                          color:'#111827', 
                          marginBottom:12,
                          textTransform:'uppercase'
                        }}>
                          {categoryName} ({items.length} items)
                        </h4>
                        <div style={{ display:'grid', gap:8 }}>
                          {items.map((item) => (
                            <div key={item.id} style={{ 
                              display:'flex', 
                              alignItems:'center', 
                              justifyContent:'space-between',
                              padding:'8px 0',
                              borderBottom:'1px solid #f3f4f6'
                            }}>
                              <div style={{ flex:1 }}>
                                <div style={{ fontSize:14, fontWeight:500, color:'#111827' }}>
                                  {item.name}
                                </div>
                                {item.description && (
                                  <div style={{ fontSize:12, color:'#6b7280', marginTop:2 }}>
                                    {item.description}
                                  </div>
                                )}
                              </div>
                              <div style={{ fontSize:14, fontWeight:600, color:'#059669' }}>
                                ₹{parseFloat(item.price).toFixed(2)}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ) : (
              <div style={{ display:'grid', gridTemplateColumns:'1fr', gap:16 }}>
                {categoryNames.map((categoryName) => {
                  const items = categories[categoryName] || [];
                  return (
                    <button
                      key={categoryName}
                      onClick={() => setSelectedCategory(categoryName)}
                      style={{ 
                        position:'relative', 
                        cursor:'pointer', 
                        borderRadius:24, 
                        overflow:'hidden', 
                        height:180, 
                        marginBottom:20, 
                        boxShadow:'0 10px 20px rgba(0,0,0,.08)', 
                        border:'none', 
                        width:'100%',
                        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'
                      }}
                    >
                      <div style={{ position:'absolute', inset:0, background:'rgba(0,0,0,0.3)', zIndex:10 }} />
                      <div style={{ position:'absolute', inset:0, zIndex:20, display:'flex', alignItems:'center', justifyContent:'center', textAlign:'center' }}>
                        <div style={{ color:'#fff', fontSize:20, fontWeight:700, textTransform:'uppercase', textShadow:'1px 1px 2px rgba(0,0,0,0.5)' }}>{categoryName}</div>
                        <div style={{ color:'#fff', fontSize:14, fontWeight:500, marginTop:8, textShadow:'1px 1px 2px rgba(0,0,0,0.5)' }}>{items.length} items</div>
                      </div>
                    </button>
                  );
                })}
              </div>
            )}
          </div>
        ) : (
          <div>
            <div style={{ display:'flex', alignItems:'center', gap:12, marginBottom:8 }}>
              <button onClick={() => { setSelectedCategory(null); setSearchTerm(''); }} style={{ display:'inline-flex', alignItems:'center', gap:8, padding:'8px 12px', borderRadius:9999, background:'#eee', color:'#374151', border:'none' }}>
                <span style={{ fontSize:18 }}>←</span>
                <span style={{ fontWeight:600 }}>Back</span>
              </button>
              <h2 style={{ fontSize:22, fontWeight:800, color:'#111827', textTransform:'uppercase', marginLeft:'auto' }}>{selectedCategory}</h2>
            </div>

            {/* Category-specific Search Bar */}
            <div style={{ marginBottom:16 }}>
              <div style={{ 
                display:'flex', 
                alignItems:'center', 
                background:'#fff', 
                borderRadius:25, 
                padding:'12px 16px', 
                boxShadow:'0 2px 8px rgba(0,0,0,0.1)',
                border:'1px solid #e5e7eb'
              }}>
                <svg style={{ width:20, height:20, color:'#6b7280', marginRight:12 }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
                <input
                  type="text"
                  placeholder={`Search in ${selectedCategory}...`}
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  style={{
                    flex:1,
                    border:'none',
                    outline:'none',
                    fontSize:16,
                    color:'#374151',
                    background:'transparent'
                  }}
                />
                {searchTerm && (
                  <button
                    onClick={() => setSearchTerm('')}
                    style={{
                      background:'none',
                      border:'none',
                      color:'#6b7280',
                      cursor:'pointer',
                      padding:4,
                      marginLeft:8
                    }}
                  >
                    <svg style={{ width:18, height:18 }} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                )}
              </div>
            </div>

            <div style={{ display:'grid', gap:12 }}>
              {(() => {
                const items = categories[selectedCategory] || [];
                const filteredItems = searchTerm 
                  ? items.filter(item => 
                      item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                      (item.description || '').toLowerCase().includes(searchTerm.toLowerCase())
                    )
                  : items;

                if (filteredItems.length === 0 && searchTerm) {
                  return (
                    <div style={{ 
                      textAlign:'center', 
                      padding:40, 
                      background:'#fff', 
                      borderRadius:12, 
                      color:'#6b7280',
                      boxShadow:'0 2px 8px rgba(0,0,0,0.1)'
                    }}>
                      No items found for "{searchTerm}" in {selectedCategory}
                    </div>
                  );
                }

                return filteredItems.map((item) => (
                  <div key={item.id} style={{ background:'#fff', borderRadius:12, boxShadow:'0 4px 10px rgba(0,0,0,.06)', border:'1px solid #e5e7eb', overflow:'hidden' }}>
                    <div style={{ padding:16 }}>
                      <div style={{ flex:1, minWidth:0 }}>
                        <div style={{ display:'flex', alignItems:'flex-start', justifyContent:'space-between', gap:8, marginBottom:4 }}>
                          <h3 style={{ fontSize:16, fontWeight:600, color:'#111827' }}>{item.name}</h3>
                          <span style={{ fontSize:18, fontWeight:700, color:'#059669', whiteSpace:'nowrap' }}>₹{parseFloat(item.price).toFixed(2)}</span>
                        </div>
                        {item.description && (<p style={{ fontSize:14, color:'#4b5563' }}>{item.description}</p>)}
                      </div>
                    </div>
                  </div>
                ));
              })()}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PublicMenu;
