import React from 'react';

const MenuItem = ({ item }) => {
  return (
    <div className="menu-item-card bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
      <div className="aspect-w-16 aspect-h-9">
        <img
          src={item.image_url}
          alt={item.name}
          className="responsive-image w-full h-48 object-cover"
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/300x200?text=No+Image';
          }}
        />
      </div>
      <div className="p-6">
        <div className="flex justify-between items-start mb-2">
          <h3 className="text-lg font-semibold text-gray-900">{item.name}</h3>
          <span className="price text-lg font-bold text-green-600">
            â‚¹{item.price.toFixed(2)}
          </span>
        </div>
        {item.description && (
          <p className="text-gray-600 text-sm leading-relaxed">
            {item.description}
          </p>
        )}
        {!item.is_available && (
          <div className="mt-2">
            <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
              Currently Unavailable
            </span>
          </div>
        )}
      </div>
    </div>
  );
};

export default MenuItem;
